package com.syntax.class02;

public class VariableCreation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 1 way to create a variable
		byte b=10;
		
		// 2 way
		int i;
		i=100;
		
		// we need to create a variable once only
		
		//3rd way
		int num1, num2, num3; // create a variable or declare a variable		
		num1=100;  // assign a value or initialize a value
		num2=200;
		num3=300;
		
		//int num1=100; error -> duplicate variable
		System.out.println(num2);
		
		num2=2000;
		
		System.out.println(num2);
		
	System.out.println("\"I love pizza\"");
	System.out.println("\\I love pizza");
	System.out.println("It's really good");
		

	}

}
