package com.syntax.class06;

public class SwitchDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name;
		int day = 0;
	switch(day) {
	case 1:
		name="Monday";
		break;
	case 2:
		name="Tuesday";
	break;
	default:
		name="Invalid";
	}
	System.out.println(name);
		
	}
	

	}


