public class Repl69 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          int n=7;
		for(int x=1; x<n; x++ ) {
			int p=1;
			for(int y=x; y<=n; y++) {
				System.out.print(p+++" "); }
			System.out.println();
		}
							
		int m=7;
		for(int i=1; i<=m; i++ ) {
			int q=1;
			for(int j=1; j<=i; j++) {
				System.out.print(q+++" "); }
			System.out.println();
				
			}
		}
	}


